### 3.3 `src/App.tsx`

```tsx
import React, { useEffect, useMemo, useState } from "react";
import { WeatherSearch } from "./components/WeatherSearch";
import { CurrentWeatherCard } from "./components/CurrentWeatherCard";
import { ForecastList } from "./components/ForecastList";
import { UnitToggle } from "./components/UnitToggle";
import { AnimatedBackground } from "./components/AnimatedBackground";
import { Loader } from "./components/Loader";
import { ErrorMessage } from "./components/ErrorMessage";
import { EmptyState } from "./components/EmptyState";
import {
  fetchWeatherByCity,
  NormalizedForecastDay,
  NormalizedWeather,
} from "./api/weatherApi";
import "./index.css";

type Unit = "metric" | "imperial";

const DEFAULT_CITY = "Warszawa";

const App: React.FC = () => {
  const [city, setCity] = useState<string | null>(null);
  const [unit, setUnit] = useState<Unit>("metric");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [current, setCurrent] = useState<NormalizedWeather | null>(null);
  const [forecast, setForecast] = useState<NormalizedForecastDay[]>([]);

  // wczytaj ostatnie miasto z localStorage
  useEffect(() => {
    const saved = localStorage.getItem("lastCity");
    if (saved) {
      setCity(saved);
    } else {
      setCity(DEFAULT_CITY);
    }
  }, []);

  // pobieranie danych przy zmianie miasta
  useEffect(() => {
    if (!city) return;
    setLoading(true);
    setError(null);

    fetchWeatherByCity(city)
      .then((data) => {
        setCurrent(data.current);
        setForecast(data.forecast);
        localStorage.setItem("lastCity", city);
      })
      .catch((err) => {
        console.error(err);
        setError("Nie uda≈Ço siƒô pobraƒá danych pogodowych. Spr√≥buj ponownie.");
        setCurrent(null);
        setForecast([]);
      })
      .finally(() => setLoading(false));
  }, [city]);

  const weatherTypeForBackground = useMemo(() => {
    if (!current) return "clear-day";
    return current.iconKey; // znormalizowany klucz z api/weatherApi
  }, [current]);

  const handleCitySelect = (newCity: string) => {
    setCity(newCity);
  };

  const handleUnitToggle = (newUnit: Unit) => {
    setUnit(newUnit);
  };

  return (
    <div className="app-root">
      <AnimatedBackground weatherType={weatherTypeForBackground} />

      <div className="app-shell">
        <header className="app-header fade-in-down">
          <div>
            <h1>Dashboard pogodowy</h1>
            <p className="subtitle">
              Nowoczesny, animowany podglƒÖd pogody dla wybranego miasta
            </p>
          </div>
          <UnitToggle unit={unit} onChange={handleUnitToggle} />
        </header>

        <main className="app-main">
          <section className="top-row slide-up">
            <WeatherSearch
              selectedCity={city ?? ""}
              onCitySelect={handleCitySelect}
            />
          </section>

          {loading && (
            <section className="center-section">
              <Loader />
            </section>
          )}

          {!loading && error && (
            <section className="center-section">
              <ErrorMessage message={error} />
            </section>
          )}

          {!loading && !error && !current && (
            <section className="center-section">
              <EmptyState />
            </section>
          )}

          {!loading && !error && current && (
            <>
              <section className="grid-two-cols fade-in">
                <CurrentWeatherCard weather={current} unit={unit} />
                <ForecastList forecast={forecast} unit={unit} />
              </section>
            </>
          )}
        </main>

        <footer className="app-footer fade-in-up">
          <span>
            Dane pogodowe:{" "}
            <a
              href="https://open-meteo.com/"
              target="_blank"
              rel="noreferrer"
            >
              Open‚ÄëMeteo
            </a>
          </span>
          <span>Interfejs: React + Vite</span>
        </footer>
      </div>
    </div>
  );
};

export default App;
```

