### 3.4 `src/api/weatherApi.ts`

Uproszczona warstwa API: geokodowanie + pogoda z Open‚ÄëMeteo (bez klucza).

```ts
// src/api/weatherApi.ts

export type NormalizedWeather = {
  city: string;
  temperatureC: number;
  feelsLikeC: number;
  windKmh: number;
  humidity: number | null;
  description: string;
  iconKey:
    | "clear-day"
    | "clear-night"
    | "cloudy"
    | "partly-cloudy"
    | "rain"
    | "snow"
    | "storm"
    | "fog";
  time: string;
};

export type NormalizedForecastDay = {
  date: string;
  minTempC: number;
  maxTempC: number;
  iconKey: NormalizedWeather["iconKey"];
  description: string;
};

type GeoResult = {
  name: string;
  latitude: number;
  longitude: number;
  country: string;
};

async function geocodeCity(city: string): Promise<GeoResult> {
  const url = new URL("https://geocoding-api.open-meteo.com/v1/search");
  url.searchParams.set("name", city);
  url.searchParams.set("count", "1");
  url.searchParams.set("language", "pl");
  url.searchParams.set("format", "json");

  const res = await fetch(url.toString());
  if (!res.ok) {
    throw new Error("B≈ÇƒÖd geokodowania");
  }
  const data = await res.json();
  if (!data.results || data.results.length === 0) {
    throw new Error("Nie znaleziono miasta");
  }
  const r = data.results[0];
  return {
    name: r.name,
    latitude: r.latitude,
    longitude: r.longitude,
    country: r.country,
  };
}

function mapWeatherCodeToIcon(code: number, isNight: boolean): NormalizedWeather["iconKey"] {
  // kody Open-Meteo: https://open-meteo.com/en/docs
  if (code === 0) return isNight ? "clear-night" : "clear-day";
  if ([1, 2].includes(code)) return "partly-cloudy";
  if (code === 3) return "cloudy";
  if ([45, 48].includes(code)) return "fog";
  if ([51, 53, 55, 56, 57, 61, 63, 65, 66, 67, 80, 81, 82].includes(code))
    return "rain";
  if ([71, 73, 75, 77, 85, 86].includes(code)) return "snow";
  if ([95, 96, 99].includes(code)) return "storm";
  return "cloudy";
}

export async function fetchWeatherByCity(city: string): Promise<{
  current: NormalizedWeather;
  forecast: NormalizedForecastDay[];
}> {
  const geo = await geocodeCity(city);

  const url = new URL("https://api.open-meteo.com/v1/forecast");
  url.searchParams.set("latitude", geo.latitude.toString());
  url.searchParams.set("longitude", geo.longitude.toString());
  url.searchParams.set("current_weather", "true");
  url.searchParams.set(
    "daily",
    "weathercode,temperature_2m_max,temperature_2m_min"
  );
  url.searchParams.set("timezone", "auto");

  const res = await fetch(url.toString());
  if (!res.ok) {
    throw new Error("B≈ÇƒÖd pobierania pogody");
  }
  const data = await res.json();

  const currentWeather = data.current_weather;
  const daily = data.daily;

  const nowCode = currentWeather.weathercode as number;
  const nowTime = currentWeather.time as string;
  const hour = parseInt(nowTime.slice(11, 13), 10);
  const isNight = hour < 6 || hour >= 20;

  const current: NormalizedWeather = {
    city: `${geo.name}, ${geo.country}`,
    temperatureC: currentWeather.temperature,
    feelsLikeC: currentWeather.temperature, // Open-Meteo nie ma odczuwalnej, przyjmujemy tƒô samƒÖ
    windKmh: currentWeather.windspeed,
    humidity: null, // brak w tym endpointcie
    description: mapCodeToDescription(nowCode),
    iconKey: mapWeatherCodeToIcon(nowCode, isNight),
    time: nowTime,
  };

  const forecast: NormalizedForecastDay[] = daily.time.slice(0, 5).map(
    (date: string, idx: number) => {
      const code = daily.weathercode[idx] as number;
      return {
        date,
        minTempC: daily.temperature_2m_min[idx],
        maxTempC: daily.temperature_2m_max[idx],
        iconKey: mapWeatherCodeToIcon(code, false),
        description: mapCodeToDescription(code),
      };
    }
  );

  return { current, forecast };
}

function mapCodeToDescription(code: number): string {
  if (code === 0) return "Bezchmurnie";
  if ([1, 2].includes(code)) return "Czƒô≈õciowe zachmurzenie";
  if (code === 3) return "Zachmurzenie";
  if ([45, 48].includes(code)) return "Mg≈Ça";
  if ([51, 53, 55].includes(code)) return "M≈ºawka";
  if ([56, 57].includes(code)) return "MarznƒÖca m≈ºawka";
  if ([61, 63, 65].includes(code)) return "Deszcz";
  if ([66, 67].includes(code)) return "MarznƒÖcy deszcz";
  if ([71, 73, 75].includes(code)) return "≈önieg";
  if (code === 77) return "≈önieg ziarnisty";
  if ([80, 81, 82].includes(code)) return "Przelotne opady deszczu";
  if ([85, 86].includes(code)) return "Przelotne opady ≈õniegu";
  if (code === 95) return "Burza";
  if ([96, 99].includes(code)) return "Burza z gradem";
  return "Pogoda zmienna";
}
