### 3.5 `src/components/WeatherSearch.tsx`

```tsx
import React, { useEffect, useState } from "react";

interface Props {
  selectedCity: string;
  onCitySelect: (city: string) => void;
}

const STORAGE_KEY = "recentCities";

export const WeatherSearch: React.FC<Props> = ({
  selectedCity,
  onCitySelect,
}) => {
  const [input, setInput] = useState(selectedCity || "");
  const [recent, setRecent] = useState<string[]>([]);

  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      setRecent(JSON.parse(saved));
    }
  }, []);

  useEffect(() => {
    setInput(selectedCity);
  }, [selectedCity]);

  const saveRecent = (city: string) => {
    const list = [city, ...recent.filter((c) => c.toLowerCase() !== city.toLowerCase())].slice(
      0,
      5
    );
    setRecent(list);
    localStorage.setItem(STORAGE_KEY, JSON.stringify(list));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const trimmed = input.trim();
    if (!trimmed) return;
    onCitySelect(trimmed);
    saveRecent(trimmed);
  };

  const handleRecentClick = (city: string) => {
    setInput(city);
    onCitySelect(city);
    saveRecent(city);
  };

  return (
    <div className="card glass search-card">
      <form onSubmit={handleSubmit} className="search-form">
        <div className="search-input-wrapper">
          <input
            type="text"
            placeholder="Wpisz miasto (np. Warszawa, Krak√≥w)..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button type="submit" className="btn-primary">
            Szukaj
          </button>
        </div>
        {recent.length > 0 && (
          <div className="recent-cities fade-in">
            <span>Ostatnio wyszukiwane:</span>
            <div className="chips">
              {recent.map((city) => (
                <button
                  key={city}
                  type="button"
                  className="chip"
                  onClick={() => handleRecentClick(city)}
                >
                  {city}
                </button>
              ))}
            </div>
          </div>
        )}
      </form>
    </div>
  );
};
```

