import React from "react";
import { NormalizedWeather } from "../api/weatherApi";
import { formatDateTime, formatTemperature } from "../utils/formatters";

interface Props {
  weather: NormalizedWeather;
  unit: "metric" | "imperial";
}

export const CurrentWeatherCard: React.FC<Props> = ({ weather, unit }) => {
  const temp = formatTemperature(weather.temperatureC, unit);
  const feels = formatTemperature(weather.feelsLikeC, unit);

  return (
    <div className="card glass current-weather-card slide-up">
      <div className="card-header">
        <h2>Aktualna pogoda</h2>
        <span className="city-name">{weather.city}</span>
      </div>

      <div className="current-main">
        <div className="current-temp">
          <div className="temp-value">{temp}</div>
          <div className="temp-desc">
            <span>{weather.description}</span>
            <span className="feels-like">Odczuwalna: {feels}</span>
            <span className="time">{formatDateTime(weather.time)}</span>
          </div>
        </div>
        <div className={`weather-icon big icon-${weather.iconKey}`} />
      </div>

      <div className="current-extra">
        <div className="extra-item">
          <span className="label">Wiatr</span>
          <span className="value">
            {weather.windKmh.toFixed(0)} km/h
          </span>
        </div>
        <div className="extra-item">
          <span className="label">Wilgotno≈õƒá</span>
          <span className="value">
            {weather.humidity != null ? `${weather.humidity}%` : "‚Äî"}
          </span>
        </div>
      </div>
    </div>
  );
};
