  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 18px;
  overflow: hidden;
}

.top-row {
  flex-shrink: 0;
}

.center-section {
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
}

.grid-two-cols {
  display: grid;
  grid-template-columns: minmax(0, 1.1fr) minmax(0, 1.1fr);
  gap: 18px;
  flex: 1;
  min-height: 0;
}

.app-footer {
  flex-shrink: 0;
  display: flex;
  justify-content: space-between;
  font-size: 0.75rem;
  color: var(--text-muted);
  padding-top: 8px;
}

.app-footer a {
  color: var(--accent);
  text-decoration: none;
}

.app-footer a:hover {
  text-decoration: underline;
}

/* Karty / glassmorphism */

.card.glass {
  background: linear-gradient(
    135deg,
    rgba(15, 23, 42, 0.9),
    rgba(15, 23, 42, 0.7)
  );
  border-radius: var(--radius-lg);
  border: 1px solid var(--glass-border);
  box-shadow: var(--shadow-soft);
  backdrop-filter: blur(var(--blur-strong));
  -webkit-backdrop-filter: blur(var(--blur-strong));
  padding: 18px 18px 16px;
  position: relative;
  overflow: hidden;
}

.card.glass::before {
  content: "";
  position: absolute;
  inset: 0;
  background: radial-gradient(
    circle at top left,
    rgba(56, 189, 248, 0.18),
    transparent 55%
  );
  opacity: 0.7;
  pointer-events: none;
}

/* Search */

.search-card {
  padding-bottom: 12px;
}

.search-form {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.search-input-wrapper {
  display: flex;
  gap: 10px;
}

.search-input-wrapper input {
  flex: 1;
  border-radius: var(--radius-md);
  border: 1px solid rgba(148, 163, 184, 0.5);
  background: rgba(15, 23, 42, 0.8);
  color: var(--text-main);
  padding: 10px 12px;
  font-size: 0.95rem;
  outline: none;
  transition: border-color var(--transition-fast),
    box-shadow var(--transition-fast), background var(--transition-fast),
    transform var(--transition-fast);
}

.search-input-wrapper input::placeholder {
  color: rgba(148, 163, 184, 0.8);
}

.search-input-wrapper input:focus {
  border-color: var(--accent);
  box-shadow: 0 0 0 1px rgba(56, 189, 248, 0.4);
  background: rgba(15, 23, 42, 0.95);
  transform: translateY(-1px);
}

.btn-primary {
  border-radius: var(--radius-md);
  border: none;
  padding: 10px 18px;
  background: linear-gradient(135deg, var(--accent), var(--accent-strong));
  color: #0b1120;
  font-weight: 600;
  font-size: 0.95rem;
  cursor: pointer;
  box-shadow: 0 10px 25px rgba(56, 189, 248, 0.4);
  transition: transform var(--transition-fast),
    box-shadow var(--transition-fast), filter var(--transition-fast);
}

.btn-primary:hover {
  transform: translateY(-1px) scale(1.02);
  box-shadow: 0 14px 35px rgba(56, 189, 248, 0.55);
  filter: brightness(1.05);
}

.btn-primary:active {
  transform: translateY(0) scale(0.98);
  box-shadow: 0 6px 18px rgba(56, 189, 248, 0.4);
}

.recent-cities {
  font-size: 0.8rem;
  color: var(--text-muted);
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.chips {
  display: flex;
  flex-wrap: wrap;
  gap: 6px;
}

.chip {
  border-radius: 999px;
  border: 1px solid rgba(148, 163, 184, 0.6);
  background: rgba(15, 23, 42, 0.8);
  color: var(--text-main);
  padding: 4px 10px;
  font-size: 0.8rem;
  cursor: pointer;
  transition: background var(--transition-fast),
    border-color var(--transition-fast), transform var(--transition-fast);
}

.chip:hover {
  background: rgba(15, 23, 42, 0.95);
  border-color: var(--accent);
  transform: translateY(-1px);
}

/* Current weather */

.current-weather-card {
  display: flex;
  flex-direction: column;
  gap: 14px;
}

.card-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  gap: 8px;
}

.card-header h2 {
  margin: 0;
  font-size: 1.1rem;
}

.city-name {
  font-size: 0.85rem;
  color: var(--text-muted);
}

.current-main {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 12px;
}

.current-temp {
  display: flex;
  align-items: center;
  gap: 12px;
}

.temp-value {
  font-size: 2.8rem;
  font-weight: 700;
  letter-spacing: 0.03em;
}

.temp-desc {
  display: flex;
  flex-direction: column;
  gap: 2px;
  font-size: 0.9rem;
}

.feels-like {
  color: var(--text-muted);
  font-size: 0.85rem;
}

.time {
  color: var(--text-muted);
  font-size: 0.8rem;
}

.current-extra {
  display: flex;
  gap: 16px;
  margin-top: 4px;
}

.extra-item {
  display: flex;
  flex-direction: column;
  gap: 2px;
  font-size: 0.85rem;
}

.extra-item .label {
  color: var(--text-muted);
}

/* Forecast */

.forecast-card {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.forecast-card h2 {
  margin: 0 0 4px;
  font-size: 1.05rem;
}

.forecast-grid {
  display: grid;
  grid-template-columns: repeat(5, minmax(0, 1fr));
  gap: 10px;
}

.forecast-item {
  border-radius: var(--radius-md);
  background: radial-gradient(
    circle at top,
    rgba(15, 23, 42, 0.95),
    rgba(15, 23, 42, 0.7)
  );
  border: 1px solid rgba(148, 163, 184, 0.4);
  padding: 10px 8px;
  display: flex;
  flex-direction: column;
  gap: 4px;
  align-items: center;
  text-align: center;
  font-size: 0.8rem;
  position: relative;
  overflow: hidden;
}

.forecast-item::before {
  content: "";
  position: absolute;
  inset: 0;
  background: radial-gradient(
    circle at top,
    rgba(56, 189, 248, 0.18),
    transparent 55%
  );
  opacity: 0;
  transition: opacity var(--transition-med);
}

.forecast-date {
  font-weight: 600;
  font-size: 0.8rem;
}

.forecast-temps {
  display: flex;
  gap: 6px;
  align-items: baseline;
}

.forecast-temps .max {
  font-weight: 600;
}

.forecast-temps .min {
  color: var(--text-muted);
  font-size: 0.75rem;
}

.forecast-desc {
  color: var(--text-muted);
  font-size: 0.75rem;
}

/* Unit toggle */

.unit-toggle {
  position: relative;
  display: inline-flex;
  align-items: center;
  padding: 3px;
  border-radius: 999px;
  background: rgba(15, 23, 42, 0.9);
  border: 1px solid rgba(148, 163, 184, 0.6);
  box-shadow: 0 10px 25px rgba(15, 23, 42, 0.8);
  overflow: hidden;
}

.unit-toggle button {
  position: relative;
  z-index: 2;
  border: none;
  background: transparent;
  color: var(--text-muted);
  padding: 4px 12px;
  font-size: 0.8rem;
  cursor: pointer;
  transition: color var(--transition-fast), transform var(--transition-fast);
}

.unit-toggle button.active {
  color: #0b1120;
  transform: translateY(-0.5px);
}

.unit-toggle-indicator {
  position: absolute;
  top: 3px;
  bottom: 3px;
  width: 50%;
  border-radius: 999px;
  background: linear-gradient(135deg, var(--accent), var(--accent-strong));
  box-shadow: 0 10px 25px rgba(56, 189, 248, 0.6);
  transition: transform var(--transition-med);
}

.unit-toggle-indicator.metric {
  transform: translateX(0%);
}

.unit-toggle-indicator.imperial {
  transform: translateX(100%);
}

/* Loader */

.loader-wrapper {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
}

.loader-orbit {
  width: 52px;
  height: 52px;
  border-radius: 999px;
  border: 2px solid rgba(148, 163, 184, 0.4);
  position: relative;
  animation: spin 1.4s linear infinite;
}

.orbit-dot {
  position: absolute;
  width: 10px;
  height: 10px;
  border-radius: 999px;
  background: var(--accent);
  top: 50%;
  left: 50%;
  transform-origin: -16px -16px;
}

.orbit-dot:nth-child(1) {
  transform: rotate(0deg) translate(-50%, -50%);
}

.orbit-dot:nth-child(2) {
  transform: rotate(120deg) translate(-50%, -50%);
  background: #a855f7;
}

.orbit-dot:nth-child(3) {
  transform: rotate(240deg) translate(-50%, -50%);
  background: #f97316;
}

.loader-wrapper p {
  font-size: 0.9rem;
  color: var(--text-muted);
}

/* Error */

.error-card {
  display: flex;
  align-items: center;
  gap: 12px;
  border-color: rgba(248, 113, 113, 0.6);
}

.error-icon {
  width: 32px;
  height: 32px;
  border-radius: 999px;
  background: rgba(248, 113, 113, 0.2);
  border: 1px solid rgba(248, 113, 113, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  color: #fecaca;
  font-weight: 700;
}

/* Empty state */

.empty-state {
  text-align: center;
  padding: 26px 18px;
}

.empty-state h2 {
  margin: 0 0 6px;
}

.empty-state p {
  margin: 0;
  font-size: 0.9rem;
  color: var(--text-muted);
}

.animated-cloud {
  width: 72px;
  height: 40px;
  margin: 0 auto 10px;
  position: relative;
}

.animated-cloud::before,
.animated-cloud::after {
  content: "";
  position: absolute;
  background: rgba(148, 163, 184, 0.9);
  border-radius: 999px;
}

.animated-cloud::before {
  width: 72px;
  height: 28px;
  bottom: 0;
  left: 0;
}

.animated-cloud::after {
  width: 40px;
  height: 40px;
  bottom: 8px;
  left: 16px;
}

.animated-cloud {
  animation: float 3s ease-in-out infinite;
}

/* Weather icons (proste, animowane) */

.weather-icon {
  position: relative;
}

.weather-icon.big {
  width: 72px;
  height: 72px;
}

.weather-icon.small {
  width: 32px;
  height: 32px;
}

/* Clear day */

.icon-clear-day::before {
  content: "";
  position: absolute;
  inset: 0;
  border-radius: 999px;
  background: radial-gradient(circle, #facc15, #f97316);
  box-shadow: 0 0 25px rgba(250, 204, 21, 0.8);
  animation: pulse 2.4s ease-in-out infinite;
}

/* Clear night */

.icon-clear-night::before {
  content: "";
  position: absolute;
  inset: 10%;
  border-radius: 999px;
  background: radial-gradient(circle at 30% 30%, #e5e7eb, #9ca3af);
  box-shadow: 0 0 18px rgba(148, 163, 184, 0.8);
}

.icon-clear-night::after {
  content: "";
  position: absolute;
  width: 60%;
  height: 60%;
  border-radius: 999px;
  background: #020617;
  top: 0;
  right: -10%;
}

/* Clouds */

.icon-cloudy::before,
.icon-partly-cloudy::before {
  content: "";
  position: absolute;
  inset: 15% 5% 10%;
  border-radius: 999px;
  background: linear-gradient(
    to bottom,
    rgba(148, 163, 184, 0.95),
    rgba(75, 85, 99, 0.95)
  );
  box-shadow: 0 0 18px rgba(148, 163, 184, 0.7);
}

.icon-partly-cloudy::after {
  content: "";
  position: absolute;
  width: 55%;
  height: 55%;
  border-radius: 999px;
  background: radial-gradient(circle, #facc15, #f97316);
  top: -10%;
  left: -5%;
  box-shadow: 0 0 18px rgba(250, 204, 21, 0.7);
}

/* Rain */

.icon-rain::before {
  content: "";
  position: absolute;
  inset: 18% 8% 20%;
  border-radius: 999px;
  background: linear-gradient(
    to bottom,
    rgba(148, 163, 184, 0.95),
    rgba(75, 85, 99, 0.95)
  );
}

.icon-rain::after {
  content: "";
  position: absolute;
  left: 18%;
  right: 18%;
  bottom: 4%;
  height: 40%;
  background-image: radial-gradient(circle, #38bdf8 1px, transparent 1px);
  background-size: 6px 8px;
  animation: rain 0.8s linear infinite;
}

/* Snow */

.icon-snow::before {
  content: "";
  position: absolute;
  inset: 18% 8% 20%;
  border-radius: 999px;
  background: linear-gradient(
    to bottom,
    rgba(209, 213, 219, 0.95),
    rgba(148, 163, 184, 0.95)
  );
}

.icon-snow::after {
  content: "";
  position: absolute;
  left: 18%;
  right: 18%;
  bottom: 4%;
  height: 40%;
  background-image: radial-gradient(circle, #e5e7eb 1px, transparent 1px);
  background-size: 6px 8px;
  animation: snow 1.4s linear infinite;
}

/* Storm */

.icon-storm::before {
  content: "";
  position: absolute;
  inset: 18% 8% 20%;
  border-radius: 999px;
  background: linear-gradient(
    to bottom,
    rgba(55, 65, 81, 0.95),
    rgba(15, 23, 42, 0.95)
  );
  box-shadow: 0 0 18px rgba(15, 23, 42, 0.9);
}

.icon-storm::after {
  content: "";
  position: absolute;
  width: 40%;
  height: 50%;
  background: linear-gradient(to bottom, #facc15, #f97316);
  clip-path: polygon(40% 0, 100% 0, 60% 55%, 100% 55%, 20% 100%, 45% 55%, 20% 55%);
  left: 30%;
  top: 30%;
  animation: flash 1.2s ease-in-out infinite;
}

/* Fog */

.icon-fog::before {
  content: "";
  position: absolute;
  inset: 25% 10%;
  border-radius: 999px;
  background: linear-gradient(
    to bottom,
    rgba(148, 163, 184, 0.8),
    rgba(75, 85, 99, 0.8)
  );
}

.icon-fog::after {
  content: "";
  position: absolute;
  left: 10%;
  right: 10%;
  bottom: 10%;
  height: 40%;
  background-image: linear-gradient(
      to right,
      rgba(148, 163, 184, 0.7) 30%,
      transparent 30%,
      transparent 70%,
      rgba(148, 163, 184, 0.7) 70%
    ),
    linear-gradient(
      to right,
      rgba(148, 163, 184, 0.5) 20%,
      transparent 20%,
      transparent 80%,
      rgba(148, 163, 184, 0.5) 80%
    );
  background-size: 100% 50%;
  background-position: 0 0, 0 50%;
  background-repeat: no-repeat;
  opacity: 0.9;
}

/* Hover animacje */

.hover-pop {
  transition: transform var(--transition-med), box-shadow var(--transition-med);
}

.hover-pop:hover {
  transform: translateY(-4px) scale(1.02);
  box-shadow: 0 18px 40px rgba(15, 23, 42, 0.9);
}

.hover-pop:hover::before {
  opacity: 1;
}

/* Animated background */

.animated-bg {
  position: fixed;
  inset: 0;
  overflow: hidden;
  z-index: 0;
  pointer-events: none;
}

.bg-layer {
  position: absolute;
  inset: -20%;
  background: radial-gradient(circle at 10% 0, #0ea5e9 0, transparent 55%),
    radial-gradient(circle at 90% 100%, #a855f7 0, transparent 55%);
  opacity: 0.6;
  filter: blur(40px);
  animation: bg-move 18s ease-in-out infinite alternate;
}

.bg-layer-2 {
  background: radial-gradient(circle at 0 100%, #22c55e 0, transparent 55%),
    radial-gradient(circle at 100% 0, #f97316 0, transparent 55%);
  opacity: 0.4;
  animation-duration: 26s;
}

.bg-particles {
  position: absolute;
  inset: 0;
  background-image: radial-gradient(circle, rgba(148, 163, 184, 0.2) 1px, transparent 1px);
  background-size: 120px 120px;
  opacity: 0.25;
  animation: particles 40s linear infinite;
}

/* R√≥≈ºne warianty t≈Ça */

.animated-bg-clear-day .bg-layer {
  filter: blur(32px);
}

.animated-bg-clear-night .bg-layer {
  background: radial-gradient(circle at 10% 0, #38bdf8 0, transparent 55%),
    radial-gradient(circle at 90% 100%, #1d4ed8 0, transparent 55%);
}

.animated-bg-rain .bg-layer {
  background: radial-gradient(circle at 10% 0, #0ea5e9 0, transparent 55%),
    radial-gradient(circle at 90% 100%, #1e293b 0, transparent 55%);
}

.animated-bg-snow .bg-layer {
  background: radial-gradient(circle at 10% 0, #e5e7eb 0, transparent 55%),
    radial-gradient(circle at 90% 100%, #38bdf8 0, transparent 55%);
}

.animated-bg-storm .bg-layer {
  background: radial-gradient(circle at 10% 0, #f97316 0, transparent 55%),
    radial-gradient(circle at 90% 100%, #1e293b 0, transparent 55%);
}

.animated-bg-fog .bg-layer {
  background: radial-gradient(circle at 10% 0, #9ca3af 0, transparent 55%),
    radial-gradient(circle at 90% 100%, #4b5563 0, transparent 55%);
}

/* Animacje wej≈õcia */

.fade-in {
  animation: fade-in 0.6s ease-out;
}

.fade-in-down {
  animation: fade-in-down 0.6s ease-out;
}

.fade-in-up {
  animation: fade-in-up 0.6s ease-out;
}

.slide-up {
  animation: slide-up 0.6s ease-out;
}

.shake {
  animation: shake 0.4s ease-out;
}

/* Keyframes */

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

@keyframes float {
  0%,
  100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-6px);
  }
}

@keyframes pulse {
  0%,
  100% {
    transform: scale(1);
    opacity: 0.9;
  }
  50% {
    transform: scale(1.06);
    opacity: 1;
  }
}

@keyframes rain {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 0 8px;
  }
}

@keyframes snow {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 4px 8px;
  }
}

@keyframes flash {
  0%,
  100% {
    opacity: 0.7;
  }
  50% {
    opacity: 1;
    filter: drop-shadow(0 0 12px rgba(250, 204, 21, 0.9));
  }
}

@keyframes bg-move {
  0% {
    transform: translate3d(0, 0, 0) scale(1);
  }
  100% {
    transform: translate3d(-4%, 4%, 0) scale(1.1);
  }
}

@keyframes particles {
  0% {
    transform: translate3d(0, 0, 0);
  }
  100% {
    transform: translate3d(-60px, -60px, 0);
  }
}

@keyframes fade-in {
  from {
    opacity: 0;
    transform: translateY(4px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fade-in-down {
  from {
    opacity: 0;
    transform: translateY(-8px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fade-in-up {
  from {
    opacity: 0;
    transform: translateY(8px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slide-up {
  from {
    opacity: 0;
    transform: translateY(12px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes shake {
  0% {
    transform: translateX(0);
  }
  25% {
    transform: translateX(-4px);
  }
  50% {
    transform: translateX(4px);
  }
  75% {
    transform: translateX(-2px);
  }
  100% {
    transform: translateX(0);
  }
}

/* Responsywno≈õƒá */

@media (max-width: 900px) {
  .grid-two-cols {
    grid-template-columns: minmax(0, 1fr);
  }

  .app-shell {
    padding-inline: 12px;
  }
}

@media (max-width: 640px) {
  .app-header {
    flex-direction: column;
    align-items: flex-start;
  }

  .search-input-wrapper {
    flex-direction: column;
  }

  .forecast-grid {
    grid-template-columns: repeat(3, minmax(0, 1fr));
  }

  body {
    overflow: auto;
  }
}
