}

/* Animated background */

.animated-bg {
  position: fixed;
  inset: 0;
  overflow: hidden;
  z-index: 0;
  pointer-events: none;
}

.bg-layer {
  position: absolute;
  inset: -20%;
  background: radial-gradient(circle at 10% 0, #0ea5e9 0, transparent 55%),
    radial-gradient(circle at 90% 100%, #a855f7 0, transparent 55%);
