## 4. Plik `script.js`

Tu jest ca≈Ça logika: losowanie pogody, prognozy, prze≈ÇƒÖcznik ¬∞C/¬∞F, zmiana t≈Ça i ikon.

```js
// Proste "API" lokalne ‚Äì generuje losowƒÖ, ale sensownƒÖ pogodƒô

const CONDITIONS = [
  {
    key: "clear-day",
    name: "S≈Çonecznie",
    tempRange: [18, 32],
    windRange: [5, 20],
    humidityRange: [30, 55],
  },
  {
    key: "clear-night",
    name: "Bezchmurna noc",
    tempRange: [10, 20],
    windRange: [2, 15],
    humidityRange: [40, 70],
  },
  {
    key: "partly-cloudy",
    name: "Czƒô≈õciowe zachmurzenie",
    tempRange: [12, 26],
    windRange: [5, 25],
    humidityRange: [40, 70],
  },
  {
    key: "cloudy",
    name: "Zachmurzenie",
    tempRange: [8, 20],
    windRange: [5, 30],
    humidityRange: [50, 80],
  },
  {
    key: "rain",
    name: "Deszcz",
    tempRange: [6, 18],
    windRange: [10, 40],
    humidityRange: [70, 95],
  },
  {
    key: "snow",
    name: "≈önieg",
    tempRange: [-8, 2],
    windRange: [5, 30],
    humidityRange: [70, 95],
  },
  {
    key: "storm",
    name: "Burza",
    tempRange: [14, 26],
    windRange: [20, 60],
    humidityRange: [70, 95],
  },
  {
    key: "fog",
    name: "Mg≈Ça",
    tempRange: [0, 10],
    windRange: [0, 10],
    humidityRange: [80, 100],
  },
];

let currentUnit = "metric"; // "metric" | "imperial"
let currentConditionKey = "clear-day";

function randInRange(min, max) {
  return min + Math.random() * (max - min);
}

function randInt(min, max) {
  return Math.round(randInRange(min, max));
}

function pickRandom(arr) {
  return arr[Math.floor(Math.random() * arr.length)];
}

function generateCurrentWeather(city) {
  const hour = new Date().getHours();
  const isNight = hour < 6 || hour >= 21;

  let condition = pickRandom(CONDITIONS);
  if (isNight && (condition.key === "clear-day" || condition.key === "partly-cloudy")) {
    condition = CONDITIONS.find((c) => c.key === "clear-night") || condition;
  }

  currentConditionKey = condition.key;

  const tempC = randInRange(condition.tempRange[0], condition.tempRange[1]);
  const feelsC = tempC + randInRange(-2, 2);
  const wind = randInRange(condition.windRange[0], condition.windRange[1]);
  const humidity = randInt(condition.humidityRange[0], condition.humidityRange[1]);

  return {
    city,
    description: condition.name,
    iconKey: condition.key,
    temperatureC: tempC,
    feelsLikeC: feelsC,
    windKmh: wind,
    humidity,
    time: new Date(),
  };
}

function generateForecast() {
  const days = [];
  const baseIndex = CONDITIONS.findIndex((c) => c.key === currentConditionKey);
  for (let i = 1; i <= 5; i++) {
    const cond =
      CONDITIONS[(baseIndex + i + CONDITIONS.length) % CONDITIONS.length];
    const min = randInRange(cond.tempRange[0], cond.tempRange[1] - 3);
    const max = min + randInRange(3, 8);
    const date = new Date();
    date.setDate(date.getDate() + i);
    days.push({
      date,
      minTempC: min,
      maxTempC: max,
      iconKey: cond.key,
      description: cond.name,
    });
  }
  return days;
}

function formatTemperature(tempC, unit) {
  if (unit === "metric") return `${Math.round(tempC)}¬∞C`;
  const f = tempC * 1.8 + 32;
  return `${Math.round(f)}¬∞F`;
}

function formatDateTime(date) {
  return date.toLocaleString("pl-PL", {
    weekday: "short",
    hour: "2-digit",
    minute: "2-digit",
  });
}

function formatDateShort(date) {
  return date.toLocaleDateString("pl-PL", {
    weekday: "short",
    day: "2-digit",
    month: "2-digit",
  });
}

// DOM

const cityForm = document.getElementById("cityForm");
const cityInput = document.getElementById("cityInput");
const emptyStateSection = document.getElementById("emptyStateSection");
const dataSection = document.getElementById("dataSection");
const cityNameEl = document.getElementById("cityName");
const currentTempEl = document.getElementById("currentTemp");
const currentDescEl = document.getElementById("currentDesc");
const feelsLikeEl = document.getElementById("feelsLike");
const currentTimeEl = document.getElementById("currentTime");
const windValueEl = document.getElementById("windValue");
const humidityValueEl = document.getElementById("humidityValue");
const currentIconEl = document.getElementById("currentIcon");
const forecastGridEl = document.getElementById("forecastGrid");
const unitToggleEl = document.getElementById("unitToggle");
const animatedBgEl = document.getElementById("animated-bg");

let lastCurrentWeather = null;
let lastForecast = [];

// Render

function renderCurrent() {
  if (!lastCurrentWeather) return;
  const w = lastCurrentWeather;
  cityNameEl.textContent = w.city;
  currentTempEl.textContent = formatTemperature(w.temperatureC, currentUnit);
  currentDescEl.textContent = w.description;
  feelsLikeEl.textContent =
    "Odczuwalna: " + formatTemperature(w.feelsLikeC, currentUnit);
  currentTimeEl.textContent = formatDateTime(w.time);
  windValueEl.textContent = `${w.windKmh.toFixed(0)} km/h`;
  humidityValueEl.textContent = `${w.humidity}%`;

  currentIconEl.className = "weather-icon big";
  currentIconEl.classList.add(`icon-${w.iconKey}`);

  // zmiana t≈Ça
  animatedBgEl.className = `animated-bg animated-bg-${w.iconKey}`;
}

function renderForecast() {
  forecastGridEl.innerHTML = "";
  lastForecast.forEach((day) => {
    const item = document.createElement("div");
    item.className = "forecast-item";

    const dateEl = document.createElement("div");
    dateEl.className = "forecast-date";
    dateEl.textContent = formatDateShort(day.date);

    const iconEl = document.createElement("div");
    iconEl.className = `weather-icon small icon-${day.iconKey}`;

    const tempsEl = document.createElement("div");
    tempsEl.className = "forecast-temps";
    const maxEl = document.createElement("span");
    maxEl.className = "max";
    maxEl.textContent = formatTemperature(day.maxTempC, currentUnit);
    const minEl = document.createElement("span");
    minEl.className = "min";
    minEl.textContent = formatTemperature(day.minTempC, currentUnit);
    tempsEl.appendChild(maxEl);
    tempsEl.appendChild(minEl);

    const descEl = document.createElement("div");
    descEl.className = "forecast-desc";
    descEl.textContent = day.description;

    item.appendChild(dateEl);
    item.appendChild(iconEl);
    item.appendChild(tempsEl);
    item.appendChild(descEl);

    forecastGridEl.appendChild(item);
  });
}

function showDataSection() {
  emptyStateSection.style.display = "none";
  dataSection.style.display = "grid";
}

// Obs≈Çuga formularza

cityForm.addEventListener("submit", (e) => {
  e.preventDefault();
  const city = (cityInput.value || "").trim() || "Twoje miasto";
  simulateWeather(city);
});

// Klikniƒôcie w przyk≈Çadowe miasto

document.querySelectorAll(".chip[data-city]").forEach((chip) => {
  chip.addEventListener("click", () => {
    const city = chip.getAttribute("data-city");
    cityInput.value = city;
    simulateWeather(city);
  });
});

// Prze≈ÇƒÖcznik jednostek

unitToggleEl.addEventListener("click", (e) => {
  const btn = e.target.closest("button[data-unit]");
  if (!btn) return;
  const unit = btn.getAttribute("data-unit");
  if (!unit || unit === currentUnit) return;
  currentUnit = unit;

  unitToggleEl
    .querySelectorAll("button[data-unit]")
    .forEach((b) => b.classList.remove("active"));
  btn.classList.add("active");

  const indicator = unitToggleEl.querySelector(".unit-toggle-indicator");
  indicator.className = `unit-toggle-indicator ${currentUnit}`;

  renderCurrent();
  renderForecast();
});

// G≈Ç√≥wna funkcja symulacji

function simulateWeather(city) {
  // ma≈Ça animacja "≈Çadowania" ‚Äì op√≥≈∫nienie
  cityForm.classList.add("loading");
  setTimeout(() => {
    cityForm.classList.remove("loading");
    lastCurrentWeather = generateCurrentWeather(city);
    lastForecast = generateForecast();
    renderCurrent();
    renderForecast();
    showDataSection();
  }, 400);
}

// Start ‚Äì opcjonalnie zasymuluj od razu Warszawƒô
setTimeout(() => {
  simulateWeather("Warszawa");
}, 600);
```

